import React, { useState } from "react";
import {
 Button,
 Grid,
 IconButton,
 MenuItem,
 Paper,
 TextField,
 Typography,
} from "@mui/material";

import AddIcon from "@mui/icons-material/Add";
import RemoveIcon from "@mui/icons-material/Remove";

import companyLogo from "./images/Vwits Logo.jpeg";
import departmentLogo from "./images/Skoda Logo.png";

import FormControlLabel from "@mui/material/FormControlLabel";
import Checkbox from "@mui/material/Checkbox";
import axios from "axios";

const AcceptanceCriteriaForm = () => {
 const [selectedDepartment, setSelectedDepartment] = useState("");
 const [subDepartments, setSubDepartments] = useState([]);
 const [projectName, setProjectName] = useState("");
 const [date, setDate] = useState("");
 const [fromDate, setFromDate] = useState("");
 const [toDate, setToDate] = useState("");
 const [agreementNumber, setAgreementNumber] = useState("");
 const [responsiblePerson, setResponsiblePerson] = useState("");
 const [serviceProvider, setServiceProvider] = useState("");
 const [serviceReceiver, setServiceReceiver] = useState("");
 const [projectDescription, setProjectDescription] = useState("");

 const handleDepartmentChange = (event) => {
  const department = event.target.value;
  setSelectedDepartment(department);
  const subDepartmentOptions = getSubDepartmentOptions(department);
  setSubDepartments(subDepartmentOptions);
 };

 const handleFormSubmit = async () => {
  const formData = {
   projectId: "",
   department: selectedDepartment,
   subDeprtmt: "",
   projectName: projectName,
   date: date,
   fromDate: fromDate,
   toDate: toDate,
   agrmntNumber: agreementNumber,
   ordrNumber: "",
   respPersonal: responsiblePerson,
   srvcProvider: serviceProvider,
   srvcReceiver: serviceReceiver,
   prjctDesc: projectDescription,
   srvcCost: 0,
   srvcMonthlyCost: 0,
   srvcRemainBdgt: 0,
   miscCost: 0,
   miscPricing: 0,
   miscMonthlyBdgt: 0,
   miscRemainBdgt: 0,
   totalCost: 0,
   totalMonthlyBdgt: 0,
   totalRemainBdgt: 0,
   level: 2,
   member: 6,
   pricing: 50000,
  };
  try {
   const response = await axios.post(
    "http://localhost:8080/acceptance/create-project",
    formData
   );
   console.log("Response from backend:", response.data);
   // Optionally, handle success response
  } catch (error) {
   console.error("Error submitting form:", error);
   // Optionally, handle error response
  }
 };

 const getSubDepartmentOptions = (department) => {
  switch (department) {
   case "KD":
    return [
     "Digital Workplace",
     "Engineering",
     "Production, Logistics & Sales",
     "SDC",
    ];
   case "SAP":
    return ["After Sales", "Procurement", "Region China & APAC"];
   case "CTO":
    return ["CTO"];
   case "RPA":
    return ["RPA"];
   case "Brands & Regions":
    return ["Digital Products", "Skoda ITS", "Other Brands & Regions"];
   default:
    return [];
  }
 };

 return (
  <form>
   <Paper
    elevation={3}
    style={{
     padding: "10px",
     margin: "20px",
     textAlign: "center",
     maxWidth: "60%",
     marginLeft: "auto",
     marginRight: "auto",
    }}
   >
    <div
     style={{
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
     }}
    >
     <img
      src={companyLogo}
      alt="Company Logo"
      style={{ height: "90px", marginRight: "auto" }}
     />
     <Typography variant="h3" gutterBottom>
      Acceptance Criteria
     </Typography>
     <img
      src={departmentLogo}
      alt="Department Logo"
      style={{ height: "110px", marginLeft: "auto" }}
     />
    </div>

    <Grid container spacing={2}>
     <Grid item xs={12} md={3}>
      <SelectField
       label="Department"
       options={["KD", "SAP", "CTO", "RPA", "Brands & Regions"]}
       value={selectedDepartment}
       onChange={handleDepartmentChange}
      />
     </Grid>
     <Grid item xs={12} md={3}>
      <SelectField label="Sub-Department" options={subDepartments} />
     </Grid>

     <Grid item xs={12} md={3}>
      <TextField
       label="Project Name"
       fullWidth
       value={projectName}
       onChange={(e) => setProjectName(e.target.value)}
      />
     </Grid>
     <Grid item xs={12} md={3}>
      <TextField
       type="date"
       label="Date"
       fullWidth
       value={date}
       onChange={(e) => setDate(e.target.value)}
       InputLabelProps={{ shrink: true }}
      />
     </Grid>

     <Grid item xs={12} md={6}>
      <DateRangeField label="Billing Period" />
     </Grid>
     <Grid item xs={12} md={6}>
      <TextField
       label="Agreement Number"
       fullWidth
       value={agreementNumber}
       onChange={(e) => setAgreementNumber(e.target.value)}
      />
     </Grid>

     <Grid item xs={12} md={3}>
      <TextField
       label="Order Number"
       fullWidth
       disabled
       value="Auto generated"
      />
     </Grid>
     <Grid item xs={12} md={3}>
      <TextField
       label="Responsible Person"
       fullWidth
       value={responsiblePerson}
       onChange={(e) => setResponsiblePerson(e.target.value)}
      />
     </Grid>

     <Grid item xs={12} md={3}>
      <TextField
       label="Service Provider"
       fullWidth
       value={serviceProvider}
       onChange={(e) => setServiceProvider(e.target.value)}
      />
     </Grid>
     <Grid item xs={12} md={3}>
      <TextField
       label="Service Receiver"
       fullWidth
       value={serviceReceiver}
       onChange={(e) => setServiceReceiver(e.target.value)}
      />
     </Grid>

     <Grid item xs={12}>
      <TextField
       label="Project Description"
       multiline
       rows={3}
       fullWidth
       value={projectDescription}
       onChange={(e) => setProjectDescription(e.target.value)}
      />
     </Grid>

     <Grid item xs={12}>
      <CostingTable />
     </Grid>

     <Grid item xs={12}>
      <DeclarationCheckbox label="Declaration of Correct Details" />
     </Grid>

     <Grid item xs={12} md={6}>
      <TextField label="Name of Manager" fullWidth />
     </Grid>

     <Grid item xs={12} md={6}>
      <TextField label="Name of the Client" fullWidth />
     </Grid>

     <Grid item xs={12}>
      <Button
       variant="contained"
       color="primary"
       onClick={handleFormSubmit}
      >
       Submit
      </Button>
     </Grid>
    </Grid>
   </Paper>
  </form>
 );
};

const SelectField = ({ label, options, value, onChange }) => (
 <TextField select label={label} fullWidth value={value} onChange={onChange}>
  {options.map((option) => (
   <MenuItem key={option} value={option}>
    {option}
   </MenuItem>
  ))}
 </TextField>
);

const DateField = ({ label }) => (
 <TextField
  type="date"
  label={label}
  fullWidth
  InputLabelProps={{ shrink: true }}
 />
);

const DateRangeField = ({ label }) => (
 <Grid container spacing={1}>
  <Grid item xs={6}>
   <DateField label={label + " From"} />
  </Grid>
  <Grid item xs={6}>
   <DateField label={label + " To"} />
  </Grid>
 </Grid>
);

const CostingTable = () => (
 <Paper
  elevation={3}
  style={{ padding: "5px", margin: "5px", marginBottom: "15px" }}
 >
  <div
   style={{ display: "flex", alignItems: "center", marginBottom: "15px" }}
  >
   <div style={{ flex: 1, backgroundColor: "#93014A", height: "3px" }} />
   <p style={{ margin: "0 10px", fontSize: "20px" }}>Cost Management</p>
   <div style={{ flex: 1, backgroundColor: "#93014A", height: "3px" }} />
  </div>

  <Grid container spacing={1}>
   {/* Header */}
   <Grid item xs={2.1}>
    <Typography variant="body1"></Typography>
   </Grid>
   <Grid item xs={3.3}>
    <Typography variant="body1">Total budget</Typography>
   </Grid>
   <Grid item xs={3.3}>
    <Typography variant="body1">Current Month budget</Typography>
   </Grid>
   <Grid item xs={3.3}>
    <Typography variant="body1">Remaining Budget</Typography>
   </Grid>

   {/* Service Cost */}
   <Grid item xs={12}>
    <Grid container spacing={1}>
     <Grid item xs={2.1} alignSelf={"center"}>
      <Typography variant="body1">Service Cost</Typography>
     </Grid>
     <Grid item xs={3.3}>
      <TextField label="€" fullWidth />
     </Grid>
     <Grid item xs={3.3}>
      <TextField label="€" fullWidth disabled />
     </Grid>
     <Grid item xs={3.3}>
      <TextField label="€" fullWidth disabled />
     </Grid>
    </Grid>
   </Grid>

   {/* Miscellaneous */}
   <Grid item xs={12}>
    <Grid container spacing={1}>
     <Grid item xs={2.1} alignSelf={"center"}>
      <Typography variant="body1">Miscellaneous Cost</Typography>
     </Grid>
     <Grid item xs={3.3}>
      <TextField label="€" fullWidth />
     </Grid>
     <Grid item xs={3.3}>
      <TextField label="€" fullWidth disabled />
     </Grid>
     <Grid item xs={3.3}>
      <TextField label="€" fullWidth disabled />
     </Grid>
    </Grid>
   </Grid>

   {/* Total */}
   <Grid item xs={12}>
    <Grid container spacing={1} marginBottom={1.5}>
     <Grid item xs={2.1} alignSelf={"center"}>
      <Typography variant="body1">Total Cost</Typography>
     </Grid>
     <Grid item xs={3.3}>
      <TextField label="€" fullWidth />
     </Grid>
     <Grid item xs={3.3}>
      <TextField label="€" fullWidth disabled />
     </Grid>
     <Grid item xs={3.3}>
      <TextField label="€" fullWidth disabled />
     </Grid>
    </Grid>
   </Grid>
  </Grid>
 </Paper>
);

const DeclarationCheckbox = () => (
 <FormControlLabel
  control={<Checkbox />}
  label={
   <Typography variant="body2" marginTop={2}>
    We hereby declare that the information on this Acceptance Criteria is
    correct, and this invoice for this particular year will be billed for
    the given particular amount.
   </Typography>
  }
 />
);

export default AcceptanceCriteriaForm;
