import React, { useState } from "react";
import {
  Button,
  Grid,
  IconButton,
  MenuItem,
  Paper,
  TextField,
  Typography,
} from "@mui/material";

import AddIcon from "@mui/icons-material/Add";
import RemoveIcon from "@mui/icons-material/Remove";

import companyLogo from "./images/Vwits Logo.jpeg";
import departmentLogo from "./images/Skoda Logo.png";

import FormControlLabel from "@mui/material/FormControlLabel";
import Checkbox from "@mui/material/Checkbox";
import axios from "axios";

const AcceptanceCriteriaForm = () => {
  const [formData, setFormData] = useState({
    projectId: "",
    department: "",
    subDeprtmt: "",
    projectName: "",
    date: "",
    fromDate: "",
    toDate: "",
    agrmntNumber: "",
    ordrNumber: "",
    respPersonal: "",
    srvcProvider: "",
    srvcReceiver: "",
    prjctDesc: "",
    srvcCost: 0,
    srvcMonthlyCost: 0,
    srvcRemainBdgt: 0,
    miscCost: 0,
    miscPricing: 0,
    miscMonthlyBdgt: 0,
    miscRemainBdgt: 0,
    totalCost: 0,
    totalMonthlyBdgt: 0,
    totalRemainBdgt: 0,
  });

  const [selectedDepartment, setSelectedDepartment] = useState("");
  const [subDepartments, setSubDepartments] = useState([]);
  const [memberCounts, setMemberCounts] = useState([0, 0, 0, 0, 0, 0]);
  const [memberPrices, setMemberPrices] = useState([0, 0, 0, 0, 0, 0]);
  const [memberData, setMemberData] = useState([
    { level: 0, members: 0, pricing: 0 },
  ]);

  const handleDepartmentChange = (event) => {
    formData.department = event.target.value;
    setSelectedDepartment(formData.department);
    formData.subDeprtmt = getSubDepartmentOptions(formData.department);
    setSubDepartments(formData.subDeprtmt);
  };

  const handleMemberCountChange = (index, count) => {
    const updatedCounts = [...memberCounts];
    updatedCounts[index] = count;
    setMemberCounts(updatedCounts);
  };

  const handleMemberPriceChange = (index, price) => {
    const updatedPrices = [...memberPrices];
    updatedPrices[index] = price;
    setMemberPrices(updatedPrices);
  };

  const getSubDepartmentOptions = (department) => {
    switch (department) {
      case "KD":
        return [
          "Digital Workplace",
          "Engineering",
          "Production, Logistics & Sales",
          "SDC",
        ];
      case "SAP":
        return ["After Sales", "Procurement", "Region China & APAC"];
      case "CTO":
        return ["CTO"];
      case "RPA":
        return ["RPA"];
      case "Brands & Regions":
        return ["Digital Products", "Skoda ITS", "Other Brands & Regions"];
      default:
        return [];
    }
  };

  const handleAddMember = () => {
    setMemberData([...memberData, { level: 0, members: 0, pricing: 0 }]);
  };

  const handleRemoveMember = (index) => {
    const updatedMemberData = [...memberData];
    updatedMemberData.splice(index, 1);
    setMemberData(updatedMemberData);
  };

  const handleMemberChange = (index, field, value) => {
    const updatedMemberData = [...memberData];
    updatedMemberData[index][field] = value;
    setMemberData(updatedMemberData);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleFormSubmit = async () => {
    // e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:8080/acceptance/create-project",
        formData
      );
      console.log("Response from backend:", response.data);
      // Optionally, handle success response
    } catch (error) {
      console.error("Error submitting form:", error);
      // Optionally, handle error response
    }
  };

  return (
    <form>
      <Paper
        elevation={3}
        style={{
          padding: "10px",
          margin: "20px",
          textAlign: "center",
          maxWidth: "60%",
          marginLeft: "auto",
          marginRight: "auto",
        }}
      >
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
          }}
        >
          <img
            src={companyLogo}
            alt="Company Logo"
            style={{ height: "90px", marginRight: "auto" }}
          />
          <Typography variant="h3" gutterBottom>
            Acceptance Criteria
          </Typography>
          <img
            src={departmentLogo}
            alt="Department Logo"
            style={{ height: "110px", marginLeft: "auto" }}
          />
        </div>

        <Grid container spacing={2}>
          <Grid item xs={12} md={3}>
            <SelectField
              label="Department"
              options={["KD", "SAP", "CTO", "RPA", "Brands & Regions"]}
              value={formData.department}
              onChange={handleDepartmentChange}
            />
          </Grid>
          <Grid item xs={12} md={3}>
            <SelectField label="Sub-Department" options={subDepartments} />
          </Grid>

          <Grid item xs={12} md={3}>
            <SelectField
              label="Project Name"
              options={["PPSO", "AEM", "FIM"]}
            />
          </Grid>
          <Grid item xs={12} md={3}>
            <DateField label="Date" />
          </Grid>

          <Grid item xs={12} md={6}>
            <DateRangeField label="Billing Period" />
          </Grid>
          <Grid item xs={12} md={6}>
            <SelectField
              label="Agreement Number"
              options={["123_VWITS_293", "123_VWITS_294", "123_VWITS_295"]}
            />
          </Grid>

          <Grid item xs={12} md={3}>
            <TextField
              label="Order Number"
              fullWidth
              disabled
              value="Auto generated"
            />
          </Grid>
          <Grid item xs={12} md={3}>
            <TextField label="Responsible Person" fullWidth />
          </Grid>

          <Grid item xs={12} md={3}>
            <SelectField
              label="Service Provider"
              options={["Volkswagen IT Services India Pvt. Ltd."]}
            />
          </Grid>
          <Grid item xs={12} md={3}>
            <SelectField
              label="Service Receiver"
              options={["Skoda Auto A.S."]}
            />
          </Grid>

          <Grid item xs={12}>
            <TextField
              label="Project Description"
              multiline
              rows={3}
              fullWidth
            />
          </Grid>

          <Grid item xs={12}>
            <CostingTable />
          </Grid>

          <Grid item xs={12}>
            <MemberBillingDetails
              memberCounts={memberCounts}
              memberPrices={memberPrices}
              memberData={memberData}
              onMemberCountChange={handleMemberCountChange}
              onMemberPriceChange={handleMemberPriceChange}
              onAddMember={handleAddMember}
              onRemoveMember={handleRemoveMember}
              onMemberChange={handleMemberChange}
            />
          </Grid>

          <Grid item xs={12}>
            <DeclarationCheckbox label="Declaration of Correct Details" />
          </Grid>

          <Grid item xs={12} md={6}>
            <TextField label="Name of Manager" fullWidth />
          </Grid>

          <Grid item xs={12} md={6}>
            <TextField label="Name of the Client" fullWidth />
          </Grid>

          <Grid item xs={12}>
            <Button
              variant="contained"
              color="primary"
              onClick={handleFormSubmit}
            >
              Submit
            </Button>
          </Grid>
        </Grid>
      </Paper>
    </form>
  );
};

const SelectField = ({ label, options, value, onChange }) => (
  <TextField select label={label} fullWidth value={value} onChange={onChange}>
    {options.map((option) => (
      <MenuItem key={option} value={option}>
        {option}
      </MenuItem>
    ))}
  </TextField>
);

const DateField = ({ label }) => (
  <TextField
    type="date"
    label={label}
    fullWidth
    InputLabelProps={{ shrink: true }}
  />
);

const DateRangeField = ({ label }) => (
  <Grid container spacing={1}>
    <Grid item xs={6}>
      <DateField label={label + " From"} />
    </Grid>
    <Grid item xs={6}>
      <DateField label={label + " To"} />
    </Grid>
  </Grid>
);

const CostingTable = () => (
  <Paper
    elevation={3}
    style={{ padding: "5px", margin: "5px", marginBottom: "15px" }}
  >
    {/*<Typography variant="h6" gutterBottom margin={1}>
      Cost Management
    </Typography><hr/>*/}

    <div
      style={{ display: "flex", alignItems: "center", marginBottom: "15px" }}
    >
      <div style={{ flex: 1, backgroundColor: "#93014A", height: "3px" }} />
      <p style={{ margin: "0 10px", fontSize: "20px" }}>Cost Management</p>
      <div style={{ flex: 1, backgroundColor: "#93014A", height: "3px" }} />
    </div>

    <Grid container spacing={1}>
      {/* Header */}
      <Grid item xs={2.1}>
        <Typography variant="body1"></Typography>
      </Grid>
      <Grid item xs={3.3}>
        <Typography variant="body1">Total budget</Typography>
      </Grid>
      <Grid item xs={3.3}>
        <Typography variant="body1">Current Month budget</Typography>
      </Grid>
      <Grid item xs={3.3}>
        <Typography variant="body1">Remaining Budget</Typography>
      </Grid>

      {/* Service Cost */}
      <Grid item xs={12}>
        <Grid container spacing={1}>
          <Grid item xs={2.1} alignSelf={"center"}>
            <Typography variant="body1">Service Cost</Typography>
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="€" fullWidth />
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="€" fullWidth disabled />
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="€" fullWidth disabled />
          </Grid>
        </Grid>
      </Grid>

      {/* Miscellaneous */}
      <Grid item xs={12}>
        <Grid container spacing={1}>
          <Grid item xs={2.1} alignSelf={"center"}>
            <Typography variant="body1">Miscellaneous Cost</Typography>
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="€" fullWidth />
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="€" fullWidth disabled />
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="€" fullWidth disabled />
          </Grid>
        </Grid>
      </Grid>

      {/* Total */}
      <Grid item xs={12}>
        <Grid container spacing={1} marginBottom={1.5}>
          <Grid item xs={2.1} alignSelf={"center"}>
            <Typography variant="body1">Total Cost</Typography>
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="€" fullWidth />
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="€" fullWidth disabled />
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="€" fullWidth disabled />
          </Grid>
        </Grid>
      </Grid>
    </Grid>
  </Paper>
);

const MemberBillingDetails = ({
  memberCounts,
  memberPrices,
  memberData,
  onMemberCountChange,
  onMemberPriceChange,
  onAddMember,
  onRemoveMember,
  onMemberChange,
}) => (
  <Paper elevation={3} style={{ padding: "5px", margin: "5px" }}>
    {/* <Typography variant="h5" gutterBottom>
      Billing Details
    </Typography> */}

    <div style={{ display: "flex", alignItems: "center", marginBottom: "5px" }}>
      <div style={{ flex: 1, backgroundColor: "#93014A", height: "3px" }} />
      <p style={{ margin: "0 10px", fontSize: "20px" }}>Billing Details</p>
      <div style={{ flex: 1, backgroundColor: "#93014A", height: "3px" }} />
    </div>

    <Grid item xs={3.5} marginTop={1.5}>
      <Typography variant="body1">Service Cost Details</Typography>
    </Grid>

    <Grid container spacing={1}>
      {memberData.map((member, index) => (
        <Grid
          container
          item
          spacing={1}
          key={index}
          marginTop={0.001}
          marginLeft={2}
        >
          <Grid item xs={3.5}>
            <TextField
              label={`Level`}
              type="number"
              value={member.level}
              onChange={(e) => onMemberChange(index, "level", e.target.value)}
              fullWidth
            />
          </Grid>
          <Grid item xs={3.5}>
            <TextField
              label={`Members`}
              type="number"
              value={member.members}
              onChange={(e) => onMemberChange(index, "members", e.target.value)}
              fullWidth
            />
          </Grid>
          <Grid item xs={3.5}>
            <TextField
              label={`Pricing (€)`}
              type="number"
              value={member.pricing}
              onChange={(e) => onMemberChange(index, "pricing", e.target.value)}
              fullWidth
            />
          </Grid>

          <Grid item xs={0.5}>
            <IconButton
              x
              color="error"
              onClick={() => onRemoveMember(index)}
              disabled={memberData.length === 1}
              style={{
                backgroundColor: "Background",
                borderRadius: "50%",
                width: "26px",
                height: "26px",
                padding: "0",
                marginTop: "15px",
                marginLeft: "15px",
              }}
            >
              <RemoveIcon />
            </IconButton>
          </Grid>

          <Grid item xs={0.5}>
            <IconButton
              color="primary"
              startIcon={<AddIcon />}
              onClick={onAddMember}
              style={{
                backgroundColor: "Background",
                borderRadius: "50%",
                width: "26px",
                height: "26px",
                padding: "0",
                marginTop: "15px",
                marginLeft: "15px",
              }}
            >
              <AddIcon />
            </IconButton>
          </Grid>
        </Grid>
      ))}

      <Grid item xs={3.5} marginTop={0.5}>
        <Typography variant="body1">Miscellaneous Details</Typography>
      </Grid>

      {/* Justification */}
      <Grid item xs={12} marginBottom={1}>
        <Grid container spacing={1} marginLeft={2}>
          <Grid item xs={3.5} alignSelf={"center"}>
            <TextField label="Pricing (€)" fullWidth />
          </Grid>
          <Grid item xs={6.7}>
            <TextField label="Justification for Miscellaneous Cost" fullWidth />
          </Grid>
        </Grid>
      </Grid>
    </Grid>
  </Paper>
);

