import React, { useState } from 'react';
import { Button, Checkbox, FormControlLabel, Grid, MenuItem, Paper, Select, TextField, Typography } from '@mui/material';

import companyLogo from './Vwits Logo.jpeg';
import departmentLogo from './Skoda Logo.png';
import backgroundImage from './Car.jpg'

const AcceptanceCriteriaForm = () => {
  const [selectedDepartment, setSelectedDepartment] = useState('');
  const [subDepartments, setSubDepartments] = useState([]);
  const [memberCounts, setMemberCounts] = useState([0, 0, 0, 0, 0, 0]);
  const [memberPrices, setMemberPrices] = useState([0, 0, 0, 0, 0, 0]);

  const handleDepartmentChange = (event) => {
    const department = event.target.value;
    setSelectedDepartment(department);
    const subDepartmentOptions = getSubDepartmentOptions(department);
    setSubDepartments(subDepartmentOptions);
  };

  const handleMemberCountChange = (index, count) => {
    const updatedCounts = [...memberCounts];
    updatedCounts[index] = count;
    setMemberCounts(updatedCounts);
  };

  const handleMemberPriceChange = (index, price) => {
    const updatedPrices = [...memberPrices];
    updatedPrices[index] = price;
    setMemberPrices(updatedPrices);
  };

  const getSubDepartmentOptions = (department) => {
    switch (department) {
      case 'KD':
        return ['Digital Workplace', 'Engineering', 'Production, Logistics & Sales', 'SDC'];
      case 'SAP':
        return ['After Sales', 'Procurement', 'Region China & APAC'];
      case 'CTO':
        return ['CTO'];
      case 'RPA':
        return ['RPA'];
      case 'Brands & Regions':
        return ['Digital Products', 'Skoda ITS', 'Other Brands & Regions'];
      default:
        return [];
    }
  };

  return (

    // <div
    //   style={{
    //     backgroundImage: `url(${backgroundImage})`, 
    //     backgroundSize: 'cover', 
    //     minHeight: '100vh', 
    //     display: 'flex',
    //     flexDirection: 'column',
    //     justifyContent: 'center',
    //     alignItems: 'center',
    //   }}
    // >

    <Paper elevation={3} style={{ padding: '20px', margin: '20px', textAlign: 'center', maxWidth: '50%', marginLeft: 'auto', marginRight: 'auto' }}>

      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <img
          src={companyLogo}
          alt="Company Logo"
          style={{ height: '80px', marginRight: 'auto' }}
        />
        <Typography variant="h3" gutterBottom>
          Acceptance Criteria
        </Typography>
        <img
          src={departmentLogo}
          alt="Department Logo"
          style={{ height: '100px', marginLeft: 'auto' }}
        />
      </div>

      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <SelectField
            label="Department"
            options={['KD', 'SAP', 'CTO', 'RPA', 'Brands & Regions']}
            value={selectedDepartment}
            onChange={handleDepartmentChange}
          />
        </Grid>
        <Grid item xs={12} md={6}>
          <SelectField label="Sub-Department" options={subDepartments} />
        </Grid>

        <Grid item xs={12} md={6}>
          <SelectField label="Project Name" options={['PPSO', 'AEM', 'FIM']} />
        </Grid>
        <Grid item xs={12} md={6}>
          <DateField label="Date" />
        </Grid>

        <Grid item xs={12} md={6}>
          <DateRangeField label="Billing Period" />
        </Grid>
        <Grid item xs={12} md={6}>
          <SelectField label="Agreement Number" options={['123_VWITS_293', '123_VWITS_294', '123_VWITS_295']} />
        </Grid>

        <Grid item xs={12} md={6}>
          <TextField label="Responsible Person" fullWidth />
        </Grid>
        <Grid item xs={12} md={6}>
          <TextField label="Order Number" fullWidth disabled value="Auto generated by sequence wise" />
        </Grid>

        <Grid item xs={12} md={6}>
          <SelectField label="Service Provider" options={['Volkswagen IT Services India Pvt. Ltd.']} />
        </Grid>
        <Grid item xs={12} md={6}>
          <SelectField label="Service Receiver" options={['Skoda Auto A.S.']} />
        </Grid>

        <Grid item xs={12}>
          <TextField label="Project Description" multiline rows={4} fullWidth />
        </Grid>

        <Grid item xs={12}>
          <CostingTable />
        </Grid>

        <Grid item xs={12}>
          <MemberBillingDetails
            memberCounts={memberCounts}
            memberPrices={memberPrices}
            onMemberCountChange={handleMemberCountChange}
            onMemberPriceChange={handleMemberPriceChange}
          />
        </Grid>

        <Grid item xs={12}>
          <DeclarationCheckbox label="Declaration of Correct Details" />
        </Grid>

        <Grid item xs={12} md={6}>
          <TextField label="Name of Manager" fullWidth />
        </Grid>

        <Grid item xs={12} md={6}>
          <TextField label="Name of the Client" fullWidth />
        </Grid>

        <Grid item xs={12}>
          <Button variant="contained" color="primary">
            Submit
          </Button>
        </Grid>
      </Grid>
    </Paper>
    // </div>
  );
};

const SelectField = ({ label, options, value, onChange }) => (
  <TextField select label={label} fullWidth value={value} onChange={onChange}>
    {options.map((option) => (
      <MenuItem key={option} value={option}>
        {option}
      </MenuItem>
    ))}
  </TextField>
);

const DateField = ({ label }) => (
  <TextField type="date" label={label} fullWidth InputLabelProps={{ shrink: true }} />
);

const DateRangeField = ({ label }) => (
  <Grid container spacing={1}>
    <Grid item xs={6}>
      <DateField label={label + ' From'} />
    </Grid>
    <Grid item xs={6}>
      <DateField label={label + ' To'} />
    </Grid>
  </Grid>
);

const CostingTable = () => (
  <Paper elevation={3} style={{ padding: '10px', margin: '10px' }}>
    <Typography variant="h5" gutterBottom>
      Costing
    </Typography>
    <Grid container spacing={1}>

      {/* Header */}
      <Grid item xs={3}>
        <Typography variant="body2"></Typography>
      </Grid>
      <Grid item xs={3}>
        <Typography variant="body2">Total budget</Typography>
      </Grid>
      <Grid item xs={3}>
        <Typography variant="body2">Current Month budget</Typography>
      </Grid>
      <Grid item xs={3}>
        <Typography variant="body2">Remaining Budget</Typography>
      </Grid>

      {/* Service Cost */}
      <Grid item xs={12}>
        <Grid container spacing={1}>
          <Grid item xs={3} alignSelf={'center'}>
            <Typography variant="body2">Service Cost</Typography>
          </Grid>
          <Grid item xs={3}>
            <TextField label="Service Cost" fullWidth/>
          </Grid>
          <Grid item xs={3}>
            <TextField label="Service Cost" fullWidth/>
          </Grid>
          <Grid item xs={3}>
            <TextField label="Service Cost" fullWidth/>
          </Grid>
        </Grid>
      </Grid>

      {/* Miscellaneous */}
      <Grid item xs={12}>
        <Grid container spacing={1}>
          <Grid item xs={3} alignSelf={'center'}>
            <Typography variant="body2">Miscellaneous Cost</Typography>
          </Grid>
          <Grid item xs={3}>
            <TextField label="Miscellaneous Cost" fullWidth/>
          </Grid>
          <Grid item xs={3}>
            <TextField label="Miscellaneous Cost" fullWidth/>
          </Grid>
          <Grid item xs={3}>
            <TextField label="Miscellaneous Cost" fullWidth/>
          </Grid>
        </Grid>
      </Grid>

      {/* Total */}
      <Grid item xs={12}>
        <Grid container spacing={1}>
          <Grid item xs={3} alignSelf={'center'}>
            <Typography variant="body2">Total Cost</Typography>
          </Grid>
          <Grid item xs={3}>
            <TextField label="Total Cost" fullWidth/>
          </Grid>
          <Grid item xs={3}>
            <TextField label="Total Cost" fullWidth/>
          </Grid>
          <Grid item xs={3}>
            <TextField label="Total Cost" fullWidth/>
          </Grid>
        </Grid>
      </Grid>

    </Grid>
  </Paper>
);

// const MemberBillingDetails = ({ memberCounts, memberPrices, onMemberCountChange, onMemberPriceChange }) => (
//   <Paper elevation={3} style={{ padding: '10px', margin: '10px' }}>
//     <Typography variant="h5" gutterBottom>
//       Billing Details
//     </Typography>
//     <Grid container spacing={1}>

//       {memberCounts.map((count, index) => (
//         <Grid key={index} item xs={6}>
//           <Typography variant="body2">
//             Level {index}: {count} Member{count !== 1 ? 's' : ''}
//           </Typography>
//           <TextField
//             label={`Enter count for Level ${index}`}
//             type="number"
//             value={count}
//             onChange={(e) => onMemberCountChange(index, e.target.value)}
//             fullWidth
//           /><br/><br/>
//           <TextField
//             label={`Enter price for Level ${index} (€)`}
//             type="number"
//             value={memberPrices[index]}
//             onChange={(e) => onMemberPriceChange(index, e.target.value)}
//             fullWidth
//           />
//         </Grid>
//       ))}

//     </Grid>
//   </Paper>
// );

const MemberBillingDetails = ({ memberCounts, memberPrices, onMemberCountChange, onMemberPriceChange }) => (
  <Paper elevation={3} style={{ padding: '10px', margin: '10px' }}>
    <Typography variant="h5" gutterBottom>
      Billing Details
    </Typography>
    <Grid container spacing={1}>

      {[1, 2.5, 3.5, 5].map((level, index) => (
        <Grid key={index} item xs={3}>
          <Typography variant="body2">
            Level {level}: {memberCounts[index]} Member{memberCounts[index] !== 1 ? 's' : ''}
          </Typography><br/>
          <TextField
            label={`Enter count for Level ${level}`}
            type="number"
            value={memberCounts[index]}
            onChange={(e) => onMemberCountChange(index, e.target.value)}
            fullWidth
          /><br/><br/>
          <TextField
            label={`Enter price for Level ${level} (€)`}
            type="number"
            value={memberPrices[index]}
            onChange={(e) => onMemberPriceChange(index, e.target.value)}
            fullWidth
          />
        </Grid>
      ))}

    </Grid>
  </Paper>
);


const DeclarationCheckbox = () => (
  <FormControlLabel
    control={<Checkbox />}
    label={
      <Typography variant="body2">
        We hereby declare that the information on this Acceptance Criteria is correct, and this invoice for the year 2024 will be billed for the given particular amount.
      </Typography>
    }
  />
);


export default AcceptanceCriteriaForm;
