import React, { useState } from 'react';
import { Button,Grid,IconButton,MenuItem,Paper,TextField,Typography } from '@mui/material';

import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';

import companyLogo from './Vwits Logo.jpeg';
import departmentLogo from './Skoda Logo.png';

import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';

const AcceptanceCriteriaForm = () => {
  const [selectedDepartment, setSelectedDepartment] = useState('');
  const [subDepartments, setSubDepartments] = useState([]);
  const [memberCounts, setMemberCounts] = useState([0, 0, 0, 0, 0, 0]);
  const [memberPrices, setMemberPrices] = useState([0, 0, 0, 0, 0, 0]);
  const [memberData, setMemberData] = useState([{ level: 0, members: 0, pricing: 0 }]);

  const handleDepartmentChange = (event) => {
    const department = event.target.value;
    setSelectedDepartment(department);
    const subDepartmentOptions = getSubDepartmentOptions(department);
    setSubDepartments(subDepartmentOptions);
  };

  const handleMemberCountChange = (index, count) => {
    const updatedCounts = [...memberCounts];
    updatedCounts[index] = count;
    setMemberCounts(updatedCounts);
  };

  const handleMemberPriceChange = (index, price) => {
    const updatedPrices = [...memberPrices];
    updatedPrices[index] = price;
    setMemberPrices(updatedPrices);
  };

  const getSubDepartmentOptions = (department) => {
    switch (department) {
      case 'KD':
        return ['Digital Workplace', 'Engineering', 'Production, Logistics & Sales', 'SDC'];
      case 'SAP':
        return ['After Sales', 'Procurement', 'Region China & APAC'];
      case 'CTO':
        return ['CTO'];
      case 'RPA':
        return ['RPA'];
      case 'Brands & Regions':
        return ['Digital Products', 'Skoda ITS', 'Other Brands & Regions'];
      default:
        return [];
    }
  };

  const handleAddMember = () => {
    setMemberData([...memberData, { level: '', members: 0, pricing: 0 }]);
  };

  const handleRemoveMember = (index) => {
    const updatedMemberData = [...memberData];
    updatedMemberData.splice(index, 1);
    setMemberData(updatedMemberData);
  };

  const handleMemberChange = (index, field, value) => {
    const updatedMemberData = [...memberData];
    updatedMemberData[index][field] = value;
    setMemberData(updatedMemberData);
  };

  return (
    <Paper elevation={3} style={{ padding: '10px', margin: '20px', textAlign: 'center', maxWidth: '60%', marginLeft: 'auto', marginRight: 'auto' }}>

      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <img
          src={companyLogo}
          alt="Company Logo"
          style={{ height: '90px', marginRight: 'auto' }}
        />
        <Typography variant="h3" gutterBottom>
          Acceptance Criteria
        </Typography>
        <img
          src={departmentLogo}
          alt="Department Logo"
          style={{ height: '110px', marginLeft: 'auto' }}
        />
      </div>

      <Grid container spacing={2}>
        <Grid item xs={12} md={3}>
          <SelectField
            label="Department"
            options={['KD', 'SAP', 'CTO', 'RPA', 'Brands & Regions']}
            value={selectedDepartment}
            onChange={handleDepartmentChange}
          />
        </Grid>
        <Grid item xs={12} md={3}>
          <SelectField label="Sub-Department" options={subDepartments} />
        </Grid>

        <Grid item xs={12} md={3}>
          <SelectField label="Project Name" options={['PPSO', 'AEM', 'FIM']} />
        </Grid>
        <Grid item xs={12} md={3}>
          <DateField label="Date" />
        </Grid>

        <Grid item xs={12} md={6}>
          <DateRangeField label="Billing Period" />
        </Grid>
        <Grid item xs={12} md={6}>
          <SelectField label="Agreement Number" options={['123_VWITS_293', '123_VWITS_294', '123_VWITS_295']} />
        </Grid>

        <Grid item xs={12} md={3}>
          <TextField label="Order Number" fullWidth disabled value="Auto generated" />
        </Grid>
        <Grid item xs={12} md={3}>
          <TextField label="Responsible Person" fullWidth />
        </Grid>

        <Grid item xs={12} md={3}>
          <SelectField label="Service Provider" options={['Volkswagen IT Services India Pvt. Ltd.']} />
        </Grid>
        <Grid item xs={12} md={3}>
          <SelectField label="Service Receiver" options={['Skoda Auto A.S.']} />
        </Grid>

        <Grid item xs={12}>
          <TextField label="Project Description" multiline rows={3} fullWidth />
        </Grid>

        <Grid item xs={12}>
          <CostingTable />
        </Grid>

        <Grid item xs={12}>
          <MemberBillingDetails
            memberCounts={memberCounts}
            memberPrices={memberPrices}
            memberData={memberData}
            onMemberCountChange={handleMemberCountChange}
            onMemberPriceChange={handleMemberPriceChange}
            onAddMember={handleAddMember}
            onRemoveMember={handleRemoveMember}
            onMemberChange={handleMemberChange}
          />
        </Grid>

        <Grid item xs={12}>
           <DeclarationCheckbox label="Declaration of Correct Details" />
        </Grid>

        <Grid item xs={12} md={6}>
           <TextField label="Name of Manager" fullWidth />
        </Grid>

        <Grid item xs={12} md={6}>
          <TextField label="Name of the Client" fullWidth />
        </Grid>

        <Grid item xs={12}>
          <Button variant="contained" color="primary">
            Submit
          </Button>
        </Grid>
      </Grid>
    </Paper>
  );
};

const SelectField = ({ label, options, value, onChange }) => (
  <TextField select label={label} fullWidth value={value} onChange={onChange}>
    {options.map((option) => (
      <MenuItem key={option} value={option}>
        {option}
      </MenuItem>
    ))}
  </TextField>
);

const DateField = ({ label }) => (
  <TextField type="date" label={label} fullWidth InputLabelProps={{ shrink: true }} />
);

const DateRangeField = ({ label }) => (
  <Grid container spacing={1}>
    <Grid item xs={6}>
      <DateField label={label + ' From'} />
    </Grid>
    <Grid item xs={6}>
      <DateField label={label + ' To'} />
    </Grid>
  </Grid>
);

const CostingTable = () => (
  <Paper elevation={3} style={{ padding: '5px', margin: '5px', marginBottom: '15px' }}>

    {/*<Typography variant="h6" gutterBottom margin={1}>
      Cost Management
    </Typography><hr/>*/}

    <div style={{ display: "flex", alignItems: "center", margin: "5px" }}>
      <div style={{ flex: 1, backgroundColor: "black", height: "3px" }} />
      <p style={{ margin: "0 10px", fontSize: "20px" }}>Cost Management</p>
      <div style={{ flex: 1, backgroundColor: "black", height: "3px" }} />
    </div>

    <Grid container spacing={1}>

      {/* Header */}
      <Grid item xs={2.1}>
        <Typography variant="body1"></Typography>
      </Grid>
      <Grid item xs={3.3}>
        <Typography variant="body1">Total budget</Typography>
      </Grid>
      <Grid item xs={3.3}>
        <Typography variant="body1">Current Month budget</Typography>
      </Grid>
      <Grid item xs={3.3}>
        <Typography variant="body1">Remaining Budget</Typography>
      </Grid>

      {/* Service Cost */}
      <Grid item xs={12}>
        <Grid container spacing={1}>
          <Grid item xs={2.1} alignSelf={'center'}>
            <Typography variant="body1">Service Cost</Typography>
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="Service Cost" fullWidth/>
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="Service Cost" fullWidth disabled/>
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="Service Cost" fullWidth disabled/>
          </Grid>
        </Grid>
      </Grid>

      {/* Miscellaneous */}
      <Grid item xs={12}>
        <Grid container spacing={1}>
          <Grid item xs={2.1} alignSelf={'center'}>
            <Typography variant="body1">Miscellaneous Cost</Typography>
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="Miscellaneous Cost" fullWidth/>
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="Miscellaneous Cost" fullWidth disabled/>
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="Miscellaneous Cost" fullWidth disabled/>
          </Grid>
        </Grid>
      </Grid>

      {/* Total */}
      <Grid item xs={12}>
        <Grid container spacing={1} marginBottom={1.5}>
          <Grid item xs={2.1} alignSelf={'center'}>
            <Typography variant="body1">Total Cost</Typography>
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="Total Cost" fullWidth/>
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="Total Cost" fullWidth disabled/>
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="Total Cost" fullWidth disabled/>
          </Grid>
        </Grid>
      </Grid>

      {/* Justification */}
      {/*<Grid item xs={12} marginTop={1} marginBottom={1}>
        <Grid container spacing={1}>
          <Grid item xs={2.1} alignSelf={'center'}>
            <Typography variant="body1"></Typography>
          </Grid>
          <Grid item xs={9.9}>
            <TextField label="Justification for Miscellaneous Cost" fullWidth/>
          </Grid>
        </Grid>
      </Grid>*/}

    </Grid>
  </Paper>
);


const MemberBillingDetails = ({ memberCounts, memberPrices, memberData, onMemberCountChange, onMemberPriceChange, onAddMember, onRemoveMember, onMemberChange }) => (
  <Paper elevation={3} style={{ padding: '5px', margin: '5px' }}>
    {/* <Typography variant="h5" gutterBottom>
      Billing Details
    </Typography> */}
    <Grid container spacing={1}>
      <Grid item xs={3.3}>
        <Typography variant="body2">Level</Typography>
      </Grid>
      <Grid item xs={3.3}>
        <Typography variant="body2">Members</Typography>
      </Grid>
      <Grid item xs={3.3}>
        <Typography variant="body2">Pricing (€)</Typography>
      </Grid>
      <Grid item xs={2.1}>
        <Typography variant="body2">Actions</Typography>
      </Grid>

      {memberData.map((member, index) => (
        <Grid container item spacing={1} key={index}>
          <Grid item xs={3.3}>
            <TextField
              label={`Level`}
              type="text"
              value={member.level}
              onChange={(e) => onMemberChange(index, 'level', e.target.value)}
              fullWidth
            />
          </Grid>
          <Grid item xs={3.3}>
            <TextField
              label={`Members`}
              type="number"
              value={member.members}
              onChange={(e) => onMemberChange(index, 'members', e.target.value)}
              fullWidth
            />
          </Grid>
          <Grid item xs={3.3}>
            <TextField
              label={`Pricing (€)`}
              type="number"
              value={member.pricing}
              onChange={(e) => onMemberChange(index, 'pricing', e.target.value)}
              fullWidth
            />
          </Grid>
          <Grid item xs={2.1}>
            <IconButton
x              color='error'
              onClick={() => onRemoveMember(index)}
              style={{
                backgroundColor: 'Background',
                borderRadius: '50%',
                width: '26px',
                height: '26px',
                padding: '0',
                marginTop: '15px'
              }}
            >
              <RemoveIcon />
            </IconButton>
          </Grid>
        </Grid>
      ))}

      <Grid item xs={12}>
        <IconButton
          color="primary"
          startIcon={<AddIcon />}
          onClick={onAddMember}
          style={{
            backgroundColor: 'Background',
            borderRadius: '50%',
            width: '26px',
            height: '26px',
            padding: '0',
          }}
        >
          <AddIcon />
        </IconButton>
      </Grid>

      {/* Justification */}
      <Grid item xs={12} marginBottom={1}>
        <Grid container spacing={1}>
          <Grid item xs={3.3} alignSelf={'center'}>
            <TextField label="Miscellaneous Cost" fullWidth/>
          </Grid>
          <Grid item xs={8.7}>
            <TextField label="Justification for Miscellaneous Cost" fullWidth/>
          </Grid>
        </Grid>
      </Grid>

    </Grid>
  </Paper>
);

const DeclarationCheckbox = () => (
  <FormControlLabel
    control={<Checkbox />}
    label={
      <Typography variant="body2">
        We hereby declare that the information on this Acceptance Criteria is correct, and this invoice for the year 2024 will be billed for the given particular amount.
      </Typography>
    }
  />
);

export default AcceptanceCriteriaForm;
