import React, { useState } from "react";
import {
  Button,
  Grid,
  IconButton,
  MenuItem,
  Paper,
  TextField,
  Typography,
} from "@mui/material";

import AddIcon from "@mui/icons-material/Add";
import RemoveIcon from "@mui/icons-material/Remove";
import formData from "./Model";

import companyLogo from "./images/Vwits Logo.jpeg";
import departmentLogo from "./images/Skoda Logo.png";

import FormControlLabel from "@mui/material/FormControlLabel";
import Checkbox from "@mui/material/Checkbox";
import axios from "axios";

 const AcceptanceCriteriaForm = () => {
  const [formData, setFormData] = useState({
    projectId: "",
    department: "",
    subDeprtmt: "",
    projectName: "",
    date: "",
    fromDate: "",
    toDate: "",
    agrmntNumber: "",
    ordrNumber: "",
    respPersonal: "",
    srvcProvider: "",
    srvcReceiver: "",
    prjctDesc: "",
    srvcCost: 0,
    srvcMonthlyCost: 0,
    srvcRemainBdgt: 0,
    miscCost: 0,
    miscPricing: 0,
    miscMonthlyBdgt: 0,
    miscRemainBdgt: 0,
    totalCost: 0,
    totalMonthlyBdgt: 0,
    totalRemainBdgt: 0,
    manager: "",
    client: "",
    level: 0,
    member: 0,
    pricing: 0,
    justification: "",
  });

  const [memberData, setMemberData] = useState([
    { level: 0, members: 0, pricing: 0 },
  ]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };


  const handleMemberChange = (index, field, value) => {
    const updatedMemberData = [...memberData];
    updatedMemberData[index][field] = value;
    setMemberData(updatedMemberData);
  };

  const handleAddMember = () => {
    setMemberData([...memberData, { level: 0, members: 0, pricing: 0 }]);
  };

  const handleRemoveMember = (index) => {
    const updatedMemberData = [...memberData];
    updatedMemberData.splice(index, 1);
    setMemberData(updatedMemberData);
  };

  const handleFormSubmit = async () => {
    try {
      const response = await axios.post(
        "http://localhost:8080/acceptance/create-project",
        formData
      );
      console.log("Response from backend:", response.data);
      // Optionally, handle success response
    } catch (error) {
      console.error("Error submitting form:", error);
      // Optionally, handle error response
    }
  };

  return (
    <form>
      <Paper
        elevation={3}
        style={{
          padding: "10px",
          margin: "20px",
          textAlign: "center",
          maxWidth: "60%",
          marginLeft: "auto",
          marginRight: "auto",
        }}
      >
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
          }}
        >
          <img
            src={companyLogo}
            alt="Company Logo"
            style={{ height: "90px", marginRight: "auto" }}
          />
          <Typography variant="h3" gutterBottom>
            Acceptance Criteria
          </Typography>
          <img
            src={departmentLogo}
            alt="Department Logo"
            style={{ height: "110px", marginLeft: "auto" }}
          />
        </div>

        <Grid container spacing={2}>
          <Grid item xs={12} md={3}>
            <TextField
              label="Department"
              name="department"
              value={formData.department}
              onChange={handleInputChange}
              fullWidth
            />
          </Grid>
          <Grid item xs={12} md={3}>
            <TextField
              label="Sub-Department"
              name="subDeprtmt"
              value={formData.subDeprtmt}
              onChange={handleInputChange}
              fullWidth
            />
          </Grid>

          <Grid item xs={12} md={3}>
            <TextField
              label="Project Name"
              name="projectName"
              fullWidth
              value={formData.projectName}
              onChange={handleInputChange}
            />
          </Grid>
          <Grid item xs={12} md={3}>
            <TextField
              label="Date"
              type="date"
              name="date"
              InputLabelProps={{ shrink: true }}
              value={formData.date}
              onChange={handleInputChange}
              fullWidth
            />
          </Grid>

          <Grid item xs={12} md={3}>
            {/* <DateRangeField label="Billing Period" fullWidth /> */}
            <TextField
              label="From Date"
              type="date"
              name="fromDate"
              InputLabelProps={{ shrink: true }}
              value={formData.fromDate}
              onChange={handleInputChange}
              fullWidth
            />
          </Grid>
          <Grid item xs={12} md={3}>
            <TextField
              label="To Date"
              type="date"
              name="toDate"
              InputLabelProps={{ shrink: true }}
              value={formData.toDate}
              onChange={handleInputChange}
              fullWidth
            />
          </Grid>
          <Grid item xs={12} md={6}>
            <TextField
              label="Agreement Number"
              name="agrmntNumber"
              value={formData.agrmntNumber}
              onChange={handleInputChange}
              fullWidth
            />
          </Grid>

          <Grid item xs={12} md={3}>
            <TextField
              label="Order Number"
              name="ordrNumber"
              fullWidth

              value={formData.ordrNumber}
              onChange={handleInputChange}
            />
          </Grid>
          <Grid item xs={12} md={3}>
            <TextField
              label="Responsible Person"
              name="respPersonal"
              value={formData.respPersonal}
              onChange={handleInputChange}
              fullWidth
            />
          </Grid>

          <Grid item xs={12} md={3}>
            <TextField
              label="Service Provider"
              fullWidth
              name="srvcProvider"
              value={formData.srvcProvider}
              onChange={handleInputChange}
            />
          </Grid>
          <Grid item xs={12} md={3}>
            <TextField
              label="Service Receiver"
              fullWidth
              name="srvcReceiver"
              value={formData.srvcReceiver}
              onChange={handleInputChange}
            />
          </Grid>

          <Grid item xs={12}>
            <TextField
              label="Project Description"
              multiline
              rows={3}
              fullWidth
              name="prjctDesc"
              value={formData.prjctDesc}
              onChange={handleInputChange}
            />
          </Grid>

          <Grid item xs={12}>
            <CostingTable formData={formData} handleInputChange={handleInputChange}/>
          </Grid>

          <Grid item xs={12}>
            <MemberBillingDetails
              memberData={memberData}
              onAddMember={handleAddMember}
              onRemoveMember={handleRemoveMember}
              onMemberChange={handleMemberChange}
              formData={formData}
              handleInputChange={handleInputChange}
            />
          </Grid>

          <Grid item xs={12}>
            <DeclarationCheckbox />
          </Grid>

          <Grid item xs={12} md={6}>
            <TextField
              label="Name of Manager"
              fullWidth
              name="manager"
              value={formData.manager}
              onChange={handleInputChange}
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <TextField
              label="Name of the Client"
              fullWidth
              name="client"
              value={formData.client}
              onChange={handleInputChange}
            />
          </Grid>

          <Grid item xs={12}>
            <Button
              variant="contained"
              color="primary"
              onClick={handleFormSubmit}
            >
              Submit
            </Button>
          </Grid>
        </Grid>
      </Paper>
    </form>
  );
};

const SelectField = ({ label, options, value, onChange }) => (
  <TextField select label={label} fullWidth value={value} onChange={onChange}>
    {options.map((option) => (
      <MenuItem key={option} value={option}>
        {option}
      </MenuItem>
    ))}
  </TextField>
);

const DateRangeField = ({ label }) => (
  <Grid container spacing={1}>
    <Grid item xs={6}>
      <TextField
        type="date"
        label={label + " From"}
        fullWidth
        InputLabelProps={{ shrink: true }}
      />
    </Grid>
    <Grid item xs={6}>
      <TextField
        type="date"
        label={label + " To"}
        fullWidth
        InputLabelProps={{ shrink: true }}
      />
    </Grid>
  </Grid>
);

const CostingTable = ({formData, handleInputChange}) => (

  <Paper
    elevation={3}
    style={{ padding: "5px", margin: "5px", marginBottom: "15px" }}
  >
    <div
      style={{ display: "flex", alignItems: "center", marginBottom: "15px" }}
    >
      <div style={{ flex: 1, backgroundColor: "#93014A", height: "3px" }} />
      <p style={{ margin: "0 10px", fontSize: "20px" }}>Cost Management</p>
      <div style={{ flex: 1, backgroundColor: "#93014A", height: "3px" }} />
    </div>

    <Grid container spacing={1}>
      {/* Header */}
      <Grid item xs={2.1}>
        <Typography variant="body1"></Typography>
      </Grid>
      <Grid item xs={3.3}>
        <Typography variant="body1">Total budget</Typography>
      </Grid>
      <Grid item xs={3.3}>
        <Typography variant="body1">Current Month budget</Typography>
      </Grid>
      <Grid item xs={3.3}>
        <Typography variant="body1">Remaining Budget</Typography>
      </Grid>

      {/* Service Cost */}
      <Grid item xs={12}>
        <Grid container spacing={1}>
          <Grid item xs={2.1} alignSelf={"center"}>
            <Typography variant="body1">Service Cost</Typography>
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="â‚¬" name="srvcCost" value={formData.srvcCost} onChange={handleInputChange} fullWidth />
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="â‚¬"  fullWidth disabled />
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="â‚¬" fullWidth disabled />
          </Grid>
        </Grid>
      </Grid>

      {/* Miscellaneous */}
      <Grid item xs={12}>
        <Grid container spacing={1}>
          <Grid item xs={2.1} alignSelf={"center"}>
            <Typography variant="body1">Miscellaneous Cost</Typography>
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="â‚¬" name="miscCost" value={formData.miscCost} onChange={handleInputChange} fullWidth />
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="â‚¬" fullWidth disabled />
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="â‚¬" fullWidth disabled />
          </Grid>
        </Grid>
      </Grid>

      {/* Total */}
      <Grid item xs={12}>
        <Grid container spacing={1} marginBottom={1.5}>
          <Grid item xs={2.1} alignSelf={"center"}>
            <Typography variant="body1">Total Cost</Typography>
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="â‚¬" name="totalCost" value={formData.totalCost} onChange={handleInputChange} fullWidth />
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="â‚¬" fullWidth disabled />
          </Grid>
          <Grid item xs={3.3}>
            <TextField label="â‚¬" fullWidth disabled />
          </Grid>
        </Grid>
      </Grid>
    </Grid>
  </Paper>
);

const MemberBillingDetails = ({
  formData,
  handleInputChange,
  memberCounts,
  memberPrices,
  memberData,
  onMemberCountChange,
  onMemberPriceChange,
  onAddMember,
  onRemoveMember,
  onMemberChange,
