import React, { useState } from "react";
import {
  Button,
  Grid,
  MenuItem,
  Paper,
  TextField,
  Typography,
} from "@mui/material";
import axios from "axios";

const AcceptanceCriteriaForm = () => {
  const [formData, setFormData] = useState({
    projectId: "",
    department: "",
    subDeprtmt: "",
    projectName: "",
    date: "",
    fromDate: "",
    toDate: "",
    agrmntNumber: "",
    ordrNumber: "",
    respPersonal: "",
    srvcProvider: "",
    srvcReceiver: "",
    prjctDesc: "",
    srvcCost: 0,
    srvcMonthlyCost: 0,
    miscCost: 0,
    miscPricing: 0,
    member: 0,
    pricing: 0,
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:8080/acceptance/create-project",
        formData
      );
      console.log("Response from backend:", response.data);
      // Optionally, handle success response
    } catch (error) {
      console.error("Error submitting form:", error);
      // Optionally, handle error response
    }
  };

  return (
    <form onSubmit={handleFormSubmit}>
      <Paper elevation={3} style={{ padding: "10px", margin: "20px", maxWidth: "60%", marginLeft: "auto", marginRight: "auto" }}>
        <Grid container spacing={2}>
          <Grid item xs={12} md={3}>
            <TextField
              name="department"
              label="Department"
              fullWidth
              value={formData.department}
              onChange={handleInputChange}
            />
          </Grid>
          {/* Add other input fields similarly */}
          <Grid item xs={12}>
            <Button type="submit" variant="contained" color="primary">
              Submit
            </Button>
          </Grid>
        </Grid>
      </Paper>
    </form>
  );
};

export default AcceptanceCriteriaForm;
