  //   } catch (error) {
  //     console.error("Error fetching agreement data:", error);
  //     toast.error("Error fetching agreement data");
  //     throw error;
  //   }
  // },


  fetchAgreementData: async function (agreementNumber, selectedDate) {
    try {
      console.log(agreementNumber, selectedDate)
      const response = await axios.get(
        `http://localhost:8080/acceptance/${agreementNumber}?generatedDate=${selectedDate}`
      );
      console.log(response)
      return response.data;
    } catch (error) {
      console.error("Error fetching agreement data:", error);
      toast.error("Error fetching agreement data");
      throw error;
    }
  },  

  fetchMemberBillingDetails: async function (agreementNumber) {
    try {
      const response = await axios.get(
        `http://localhost:8080/acceptance/member-billing/${agreementNumber}`
      );
      return response.data;
    } catch (error) {
      console.error("Error fetching member billing details:", error);
      toast.error("Error fetching member billing details");
      throw error;
    }
  },
};

export default ApiService;
