import React from "react";
import { Button, Grid, Paper, Typography, TextField } from "@mui/material";

import companyLogo from "../Images/Vwits Logo.jpeg";
import departmentLogo from "../Images/Skoda Logo.png";

import CostingTable from "./CostingTable";
import MemberBillingDetails from "./MemberBillingDetails";
import DeclarationCheckbox from "./DeclarationCheckbox";

import { ApiService } from "../Services/ApiService";
import { FormDataService } from "../Services/FormDataService"
import { ToastContainer, toast } from "react-toastify" 

const AcceptanceCriteriaForm = () => {

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    FormDataService.updateFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleFormSubmit = async (formData) => {
    try {
      await ApiService.submitFormData(formData);
      toast.success("Data Submitted Successfully");
      setTimeout(() => {
        window.location.reload();
      }, 5000);
    } catch (error) {
      console.error("Error submitting form:", error);
    }
  };

  return (
    <>
    <ToastContainer position="bottom-center" />
      <form>
        <Paper
          elevation={3}
          style={{
            padding: "10px",
            margin: "20px",
            textAlign: "center",
            maxWidth: "55%",
            marginLeft: "auto",
            marginRight: "auto",
          }}
        >
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <img
              src={companyLogo}
              alt="Company Logo"
              style={{ height: "90px", marginRight: "auto" }}
            />
            <Typography variant="h3" gutterBottom>
              Acceptance Criteria
            </Typography>
            <img
              src={departmentLogo}
              alt="Department Logo"
              style={{ height: "110px", marginLeft: "auto" }}
            />
          </div>

          <Grid container spacing={2}>
            <Grid item xs={12} md={3}>
              <TextField
                label="Department"
                name="department"
                value={FormDataService.formData.department}
                onChange={handleInputChange}
                fullWidth
              />
            </Grid>
            <Grid item xs={12} md={3}>
              <TextField
                label="Sub-Department"
                name="subDeprtmt"
                value={FormDataService.formData.subDeprtmt}
                onChange={handleInputChange}
                fullWidth
              />
            </Grid>

            <Grid item xs={12} md={3}>
              <TextField
                label="Project Name"
                name="projectName"
                fullWidth
                value={FormDataService.formData.projectName}
                onChange={handleInputChange}
              />
            </Grid>
            <Grid item xs={12} md={3}>
              <TextField
                label="Date"
                type="date"
                name="date"
                InputLabelProps={{ shrink: true }}
                value={FormDataService.formData.date}
                onChange={handleInputChange}
                fullWidth
              />
            </Grid>

            <Grid item xs={12} md={3}>
              <TextField
                label="From Date"
                type="date"
                name="fromDate"
                InputLabelProps={{ shrink: true }}
                value={FormDataService.formData.fromDate}
                onChange={handleInputChange}
                fullWidth
              />
            </Grid>
            <Grid item xs={12} md={3}>
              <TextField
                label="To Date"
                type="date"
                name="toDate"
                InputLabelProps={{ shrink: true }}
                value={FormDataService.formData.toDate}
                onChange={handleInputChange}
                fullWidth
              />
            </Grid>

            <Grid item xs={12} md={6}>
              <TextField
                label="Agreement Number"
                name="agrmntNumber"
                value={FormDataService.formData.agrmntNumber}
                onChange={handleInputChange}
                fullWidth
              />
            </Grid>

            <Grid item xs={12} md={3}>
              <TextField
                label="Order Number"
                name="ordrNumber"
                fullWidth
                value={FormDataService.formData.ordrNumber}
                onChange={handleInputChange}
              />
            </Grid>
            <Grid item xs={12} md={3}>
              <TextField
                label="Responsible Person"
                name="respPersonal"
                value={FormDataService.formData.respPersonal}
                onChange={handleInputChange}
                fullWidth
              />
            </Grid>

            <Grid item xs={12} md={3}>
              <TextField
                label="Service Provider"
                fullWidth
                name="srvcProvider"
                value={FormDataService.formData.srvcProvider}
                onChange={handleInputChange}
              />
            </Grid>
            <Grid item xs={12} md={3}>
              <TextField
                label="Service Receiver"
                fullWidth
                name="srvcReceiver"
                value={FormDataService.formData.srvcReceiver}
                onChange={handleInputChange}
              />
            </Grid>

            <Grid item xs={12}>
              <TextField
                label="Project Description"
                multiline
                rows={3}
                fullWidth
                name="prjctDesc"
                value={FormDataService.formData.prjctDesc}
                onChange={handleInputChange}
              />
            </Grid>

            <Grid item xs={12}>
              <CostingTable />
            </Grid>
            <Grid item xs={12}>
              <MemberBillingDetails />
            </Grid>
            <Grid item xs={12}>
              <DeclarationCheckbox />
            </Grid>

            <Grid item xs={12} md={6}>
              <TextField
                label="Name of Manager"
                fullWidth
                name="mngrName"
                value={FormDataService.formData.mngrName}
                onChange={handleInputChange}
              />
            </Grid>

            <Grid item xs={12} md={6}>
              <TextField
                label="Name of the Client"
                fullWidth
                name="clientName"
                value={FormDataService.formData.clientName}
                onChange={handleInputChange}
              />
            </Grid>

            <Grid item xs={12}>
              <Button
                variant="contained"
                color="primary"
                onClick={() => handleFormSubmit(FormDataService.formData)}
              >
                Submit
              </Button>
            </Grid>
          </Grid>
        </Paper>
      </form>
    </>
  );
};

export default AcceptanceCriteriaForm;
