import React from "react";
import { Button, Grid, Paper,Typography } from "@mui/material";

import companyLogo from "./images/Vwits Logo.jpeg";
import departmentLogo from "./images/Skoda Logo.png";

import CostingTable from "./CostingTable";
import MemberBillingDetails from "./MemberBillingDetails";
import DeclarationCheckbox from "./DeclarationCheckbox";
import { ApiService } from "../services/ApiService";

const AcceptanceCriteriaForm = () => {
  const handleFormSubmit = async (formData) => {
    try {
      await ApiService.submitFormData(formData);
      toast.success("Data Submitted Successfully");
      setTimeout(() => {
        window.location.reload();
      }, 5000);
    } catch (error) {
      console.error("Error submitting form:", error);
    }
  };

  return (
    <>
      <form>
        <Paper
          elevation={3}
          style={{
            padding: "10px",
            margin: "20px",
            textAlign: "center",
            maxWidth: "55%",
            marginLeft: "auto",
            marginRight: "auto",
          }}
        >
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <img
              src={companyLogo}
              alt="Company Logo"
              style={{ height: "90px", marginRight: "auto" }}
            />
            <Typography variant="h3" gutterBottom>
              Acceptance Criteria
            </Typography>
            <img
              src={departmentLogo}
              alt="Department Logo"
              style={{ height: "110px", marginLeft: "auto" }}
            />
          </div>

          <Grid container spacing={2}>
            {/* Form fields */}
            <CostingTable />
            <MemberBillingDetails />
            <DeclarationCheckbox />

            <Grid item xs={12}>
              <Button
                variant="contained"
                color="primary"
                onClick={() => handleFormSubmit(formData)}
              >
                Submit
              </Button>
            </Grid>
          </Grid>
        </Paper>
      </form>
    </>
  );
};

export default AcceptanceCriteriaForm;
